/*
* Estos son los datos:
* "age";  "job";       "marital"; "education"; "default";  "balance"; "housing"; "loan";  "contact";  "day"; "month"; "duration";
* 58;    "management"; "married"; "tertiary";  "no";       2143;      "yes";     "no";    "unknown";  5;     "may";   261;       
*
* "campaign"; "pdays"; "previous"; "poutcome"; "y"
*  1;         -1;      0;          "unknown";  "no"
*/
//Definimos la clase. Esta es la estructura que se va a llenar con los datos del archivo
//Housing, Loan son valores 'yes' y 'no', pero los definiremos como enteros. Abajo podremos cambiar yes -> 1 y no -> 0
case class Bank(age:Integer, job:String, marital : String, education : String, balance : Integer,housing: Integer,loan: Integer)

// Aquí se parte la línea por el separador ';', se quita el encabezado y se llenan los datos a la clase que definimos
val bank = bankText.map(s=>s.split(";")).filter(s=>s(0)!="\"age\"").map(
    s=>Bank(s(0).toInt, 
            s(1).replaceAll("\"", ""),
            s(2).replaceAll("\"", ""),
            s(3).replaceAll("\"", ""),
            s(5).replaceAll("\"", "").toInt,
            s(6).replaceAll("\"", "") match {
                case "yes" => 1
                case _ => 0
            },
            s(7).replaceAll("\"", "") match {
                case "yes" => 1
                case _ => 0
            }
        )
)

// Se convierte a DataFrame y se crea una tabla temporal
bank.toDF().registerTempTable("bank")
